  /^main\.(py|js|ts|go|rs)$/,
  /^index\.(py|js|ts|tsx|jsx)$/,
  /^app\.(py|js|ts|tsx|jsx)$/,
  /^server\.(py|js|ts)$/,
  /^run\.(py|js|ts)$/,
