];

// ĞŸÑ€Ğ¸Ğ¾Ñ€Ğ¸Ñ‚ĞµÑ‚Ğ½Ñ‹Ğµ Ñ„Ğ°Ğ¹Ğ»Ñ‹
const priorityPatterns = [
  /^readme\.md$/i,
  /^package\.json$/,
  /^requirements\.txt$/,
  /^pyproject\.toml$/,
  /^main\.(py|js|ts)$/,
  /^index\.(py|js|ts)$/,
  /^app\.(py|js|ts)$/,
  /^server\.(py|js|ts)$/,
  /models?\.py$/,
  /schema\.py$/
];

