// =====================================================
// ğŸ“Š PROCESS RESULT: ĞĞ±Ñ€Ğ°Ğ±Ğ°Ñ‚Ñ‹Ğ²Ğ°ĞµĞ¼ Ñ€ĞµĞ·ÑƒĞ»ÑŒÑ‚Ğ°Ñ‚ PUT
// =====================================================
const items = $input.all();
const firstData = $('ğŸ“„ Split Pending Files').first().json;

// Ğ¡Ğ¾Ğ±Ğ¸Ñ€Ğ°ĞµĞ¼ Ñ€ĞµĞ·ÑƒĞ»ÑŒÑ‚Ğ°Ñ‚Ñ‹
const results = items.map(item => {
  const data = item.json;
  const path = data?.content?.path || null;


  // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµĞ¼ ÑƒÑĞ¿ĞµÑ…
  if (data.content && data.commit) {
    return { path, status: 'success', sha: data.content.sha };
  } else {
    const error = data.message || data.error || 'Unknown error';
    return { path, status: 'failed', error };
  }
});

console.log(results);

// ĞĞ±Ğ½Ğ¾Ğ²Ğ»ÑĞµĞ¼ ÑĞ¾ÑÑ‚Ğ¾ÑĞ½Ğ¸Ğµ Ñ„Ğ°Ğ¹Ğ»Ğ¾Ğ²
const updatedFiles = firstData.files_to_process.map(f => {
  if (f.status != 'success') {
    const result = results.find(r => r.path === f.path);
    if (result) {
      if (result.status === 'success') {
        return { ...f, status: 'success', new_sha: result.sha };
      }
    } else {
      return {
        ...f,
        attempts: f.attempts + 1,
        status: f.attempts + 1 >= firstData.max_attempts ? 'failed' : 'pending'    
      }
    }
  }
});

// ĞŸĞ¾Ğ´ÑÑ‡Ğ¸Ñ‚Ñ‹Ğ²Ğ°ĞµĞ¼ ÑÑ‚Ğ°Ñ‚Ğ¸ÑÑ‚Ğ¸ĞºÑƒ
const successCount = updatedFiles.filter(f => f.status === 'success').length;
const pendingCount = updatedFiles.filter(f => f.status === 'pending').length;
const failedCount = updatedFiles.filter(f => f.status === 'failed').length;

console.log(`After attempt ${firstData.current_attempt}: success=${successCount}, pending=${pendingCount}, failed=${failedCount}`);

// Ğ•ÑĞ»Ğ¸ ĞµÑÑ‚ÑŒ pending Ñ„Ğ°Ğ¹Ğ»Ñ‹ Ğ¸ Ğ½Ğµ Ğ¿Ñ€ĞµĞ²Ñ‹ÑĞ¸Ğ»Ğ¸ Ğ»Ğ¸Ğ¼Ğ¸Ñ‚ Ğ¿Ğ¾Ğ¿Ñ‹Ñ‚Ğ¾Ğº - Ğ¿Ñ€Ğ¾Ğ´Ğ¾Ğ»Ğ¶Ğ°ĞµĞ¼
const needRetry = pendingCount > 0 && firstData.current_attempt < firstData.max_attempts;

return [{
  json: {
    repo_owner: firstData.repo_owner,
    repo_name: firstData.repo_name,
    branch_name: firstData.branch_name,
    base_branch: firstData.base_branch,
    commit_message: firstData.commit_message,
    pr_title: firstData.pr_title,
    pr_description: firstData.pr_description,
    summary: firstData.summary,
    task_id: firstData.task_id,
    tech_stack: firstData.tech_stack,
    review_iterations: firstData.review_iterations,
    agent_results: firstData.agent_results,
    files_to_process: updatedFiles,
    current_attempt: firstData.current_attempt + 1,
    max_attempts: firstData.max_attempts,
    // Ğ¡Ñ‚Ğ°Ñ‚Ğ¸ÑÑ‚Ğ¸ĞºĞ°
    success_count: successCount,
    pending_count: pendingCount,
    failed_count: failedCount,
    need_retry: needRetry,
    all_done: !needRetry
  }
}];

// =====================================================
// â³ WAIT: ĞĞµĞ±Ğ¾Ğ»ÑŒÑˆĞ°Ñ Ğ·Ğ°Ğ´ĞµÑ€Ğ¶ĞºĞ° Ğ¿ĞµÑ€ĞµĞ´ retry
// =====================================================
