console.log(`Waiting before retry attempt ${data.current_attempt}...`);

// ĞŸÑ€Ğ¾ÑÑ‚Ğ¾ Ğ¿Ñ€Ğ¾Ğ±Ñ€Ğ°ÑÑ‹Ğ²Ğ°ĞµĞ¼ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ Ğ´Ğ°Ğ»ÑŒÑˆĞµ
return [{ json: data }];

// =====================================================
// âœ… FINALIZE: Ğ¤Ğ¸Ğ½Ğ°Ğ»ÑŒĞ½Ğ°Ñ Ğ¿Ñ€Ğ¾Ğ²ĞµÑ€ĞºĞ° Ñ€ĞµĞ·ÑƒĞ»ÑŒÑ‚Ğ°Ñ‚Ğ¾Ğ²
// =====================================================
const data = $input.first().json;
const files = data.files_to_process || [];

const successFiles = files.filter(f => f.status === 'success');
const failedFiles = files.filter(f => f.status === 'failed');

console.log(`Final: ${successFiles.length} success, ${failedFiles.length} failed`);

// Ğ•ÑĞ»Ğ¸ ĞµÑÑ‚ÑŒ failed Ñ„Ğ°Ğ¹Ğ»Ñ‹ - ÑÑ‚Ğ¾ Ğ¾ÑˆĞ¸Ğ±ĞºĞ°
