    failed_files_details: failedFiles.map(f => ({
      path: f.path,
      error: f.last_error,
      attempts: f.attempts
    })),
    has_errors: hasErrors,
    can_create_pr: successFiles.length > 0  // PR Ğ¼Ğ¾Ğ¶Ğ½Ğ¾ ÑĞ¾Ğ·Ğ´Ğ°Ñ‚ÑŒ ĞµÑĞ»Ğ¸ Ñ…Ğ¾Ñ‚ÑŒ Ñ‡Ñ‚Ğ¾-Ñ‚Ğ¾ ÑĞ¾Ğ·Ğ´Ğ°Ğ»Ğ¾ÑÑŒ
  }
}];

const data = $('âœ… Finalize Results').first().json;
